class FetchData {
  static baseURL = 'https://jsonplaceholder.typicode.com';

  static async getUsers() {
    try {
      console.log('Fazendo requisição para:', `${this.baseURL}/users`);
      const response = await fetch(`${this.baseURL}/users`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      console.log('Resposta recebida:', response.status, response.statusText);
      
      if (!response.ok) {
        throw new Error(`Erro HTTP: ${response.status} - ${response.statusText}`);
      }
      
      const data = await response.json();
      console.log('Dados recebidos:', data.length, 'usuários');
      return data;
    } catch (error) {
      console.error('Erro detalhado ao buscar usuários:', error);
      
      // Fallback com dados mock em caso de erro
      console.log('Usando dados mock como fallback...');
      return [
        {
          id: 1,
          name: "João Silva",
          email: "joao@email.com",
          phone: "(11) 99999-9999",
          company: {
            name: "Tech Corp",
            catchPhrase: "Inovação em tecnologia"
          }
        },
        {
          id: 2,
          name: "Maria Santos",
          email: "maria@email.com", 
          phone: "(11) 88888-8888",
          company: {
            name: "Digital Solutions",
            catchPhrase: "Soluções digitais para o futuro"
          }
        }
      ];
    }
  }

  static async getPostsByUserId(userId) {
    try {
      const response = await fetch(`${this.baseURL}/posts/?userId=${userId}`);
      if (!response.ok) {
        throw new Error(`Erro ao buscar posts: ${response.status}`);
      }
      return await response.json();
    } catch (error) {
      console.error('Erro ao buscar posts:', error);
      throw error;
    }
  }

  static async getCommentsByPostId(postId) {
    try {
      const response = await fetch(`${this.baseURL}/comments?postId=${postId}`);
      if (!response.ok) {
        throw new Error(`Erro ao buscar comentários: ${response.status}`);
      }
      return await response.json();
    } catch (error) {
      console.error('Erro ao buscar comentários:', error);
      throw error;
    }
  }
}

export default FetchData;
